sys_push_notif_msg_content.script:
(function buildJSON(/*GlideRecord*/ current, /*String*/ message, /*Object*/ attributes) {

	var list1 = current.uitgedaagden.toString();
	var list2 = '';
	var i = 0;
	var userGr = new GlideRecord('sys_user');
	userGr.addEncodedQuery('sys_idIN'+list1);
	userGr.query();
	while (userGr.next()){
		if (i==0){
			list2 = userGr.first_name;
			i=1;
		}
		else
			list2 = list2+", "+userGr.first_name;
	}

	var json = {};

	json = {
		"aps" : {
			"sound" : "default"
		},
		"record" : {
			"table" : current.getTableName(),
			"sys_id" : current.sys_id
		},
		"message" : current.number+": Je bent toegewezen als jury voor een weddenschap van "+current.gemaakt_door.first_name+" tegen: "+list2

	};

	return json;

})(current, message, attributes);
