sys_push_notif_msg_content.script:
(function buildJSON(/*GlideRecord*/ current, /*String*/ message, /*Object*/ attributes) {

 var json = {};

 json = {
		"aps" : {
			"sound" : "default"
		},
		"record" : {
			"table" : current.getTableName(),
			"sys_id" : current.sys_id
		},
		"message" : current.number+": Iedereen heeft de weddenschap van "+current.gemaakt_door.first_name+" geaccepteerd. Hij is nu begonnen."

	};

 return json;

})(current, message, attributes);
