sys_ui_title.script:
var aanhef = "Hij wedt dat: ";
if (gs.getUserID() == current.gemaakt_door.sys_id)
	aanhef = "Ik wed dat: ";

var omschrijving = current.omschrijving.replace(/\n/g, " ");

var title = current.number+" van "+current.gemaakt_door.first_name;
var extras = ["Inzet: "+current.inzet, "Tegen: "+current.uitgedaagden.getDisplayValue(), aanhef+omschrijving.substring(0, 90)+"..."];

titleValues.setTitle(title);
titleValues.setExtras(extras);
